// src/components/ProblemSelector.js
import React, { useState } from 'react';

const ProblemSelector = ({ onSelectProblem }) => {
  const [selectedProblem, setSelectedProblem] = useState('');

  const handleProblemChange = (problem) => {
    setSelectedProblem(problem);
    onSelectProblem(problem);
  };

  return (
    <div>
      <h2>Select Problem:</h2>
      {/* Display problem options */}
      {/* Add your problem options here */}
      <ul>
        <li onClick={() => handleProblemChange('Screen Issue')}>Screen Issue</li>
        <li onClick={() => handleProblemChange('Battery Problem')}>Battery Problem</li>
        {/* Add more problems as needed */}
      </ul>
    </div>
  );
};

export default ProblemSelector;
