// src/App.js
import React, { useState } from 'react';
import DeviceSelector from './DeviceSelector';
import ModelSelector from './ModelSelector';
import ProblemSelector from './ProblemSelector';
import ContactForm from './ContactForm';
import './style.css'; // Correct the import path if needed

function App() {
  const [selectedDevice, setSelectedDevice] = useState('');
  const [selectedModel, setSelectedModel] = useState('');
  const [selectedProblem, setSelectedProblem] = useState('');

  const handleSubmit = (userData) => {
    // Handle submitting user data (e.g., send to backend or external service)
    console.log('User Data:', userData);
    // You can implement the logic to send data to the owner's number or a server here
  };
  return (
    <div className="container fade-in">
      <h1>Mobile Support Application</h1>
      {(selectedDevice || selectedModel) ? (
        <>
          <ModelSelector selectedDevice={selectedDevice} onSelectModel={setSelectedModel} />
          {selectedModel && (
            <>
              <ProblemSelector
                selectedDevice={selectedDevice}
                onSelectProblem={setSelectedProblem}
              />
              {selectedProblem && (
                <ContactForm
                  selectedDevice={selectedDevice}
                  selectedModel={selectedModel}
                  selectedProblem={selectedProblem}
                  onSubmit={handleSubmit}
                />
              )}
            </>
          )}
        </>
      ) : (
        <DeviceSelector onSelectDevice={setSelectedDevice} />
      )}
    </div>
  );
}

export default App;
